
const selectionTime=3000;const restaurantChangeTime=100;const resetTime=500;let restaurantIndex=0;let countdownNum=3;let restaurantChangeInterval;function setupSelector(){console.log('Setting up selector.');const initElement=document.getElementById('init');initElement.style.display='none';const selectorElement=document.getElementById('selector');selectorElement.style.display='block';}
function countdown(){console.log('Beginning Countdown...');const selectorButton=document.getElementById('selector-button');selectorButton.style.display='none';const countdownElement=document.getElementById('countdown');countdownNum=3;countdownElement.innerText=countdownNum;countdownElement.style.display='block';changeNum=setInterval(()=>{console.log('In change num.');countdownNum--;countdownElement.innerText=countdownNum;if(countdownNum<=0){countdownElement.style.display='none';clearInterval(changeNum);startSelection();}},1000);}
function startSelection(){console.log('Selecting a restaurant...');const textElement=document.getElementById('selector-text');if(textElement.classList.contains('shrink')){textElement.classList.remove('shrink');}
textElement.classList.add('grow');document.body.classList.remove('red');document.body.classList.add('green');cycleRestaurant();restaurantChangeInterval=setInterval(cycleRestaurant,restaurantChangeTime);setTimeout(endSelection,selectionTime);}
function cycleRestaurant(){console.log('In cycle restaurant.');const textElement=document.getElementById('selector-text');textElement.innerText=restaurants[restaurantIndex].place;restaurantIndex++;if(restaurantIndex>=restaurants.length){restaurantIndex=0;}}
function endSelection(){console.log('In end selection.');clearInterval(restaurantChangeInterval);const restartButton=document.getElementById('restart-button');restartButton.style.display='block';const userElement=document.getElementById('user');userElement.style.display='block';userElement.innerText=restaurants[restaurantIndex].user;}
function restartSelection(){console.log('Resetting selection...');const restartButton=document.getElementById('restart-button');restartButton.style.display='none';const userElement=document.getElementById('user');userElement.style.display='none';const textElement=document.getElementById('selector-text');textElement.classList.remove('grow');textElement.classList.add('shrink');document.body.classList.remove('green');document.body.classList.add('red');setTimeout(countdown,resetTime);}